import React, { useState, useEffect } from "react"
import { Token } from "../Main"
import { useEthers, useTokenBalance } from "@usedapp/core"
import { formatUnits } from "@ethersproject/units"
import { Button, Input, CircularProgress } from "@material-ui/core"
import { useStakeTokens, useRewardsCheck, useAnswer, useClaimRewards } from "../../hooks"
import { utils } from "ethers"
import { WalletBalance } from "./WalletBalance"

export interface StakeFormProps {
    token: Token
}

export const StakeForm = ({ token }: StakeFormProps) => {
    const { address: tokenAddress, name } = token
    const { account } = useEthers()
    const tokenBalance = useTokenBalance(tokenAddress, account)
    const formattedTokenBalance: number = tokenBalance ? parseFloat(formatUnits(tokenBalance, 18)) : 0

    const [rewardsToAssign, setRewardsToAssign] = useState<number | string | Array<number | string>>(0)
    const [maxAnswers, setMaxAnswers] = useState<number | string | Array<number | string>>(0)
    const [deadline, setDeadline] = useState<number | string | Array<number | string>>(0)

    const handleInputChangeRewardsToAssign = (event: React.ChangeEvent<HTMLInputElement>) => {
        const rewardsToAssign = event.target.value === "" ? "" : Number(event.target.value)
        setRewardsToAssign(rewardsToAssign)
        console.log(rewardsToAssign)
    }

    const handleInputChangeMaxAnswers = (event: React.ChangeEvent<HTMLInputElement>) => {
        const maxAnswers = event.target.value === "" ? "" : Number(event.target.value)
        setMaxAnswers(maxAnswers)
        console.log(maxAnswers)
    }

    const handleInputChangeDeadline = (event: React.ChangeEvent<HTMLInputElement>) => {
        const deadline = event.target.value === "" ? "" : Number(event.target.value)
        setDeadline(deadline)
        console.log(deadline)
    }

    const { approveAndStake, state: approveAndStakeErc20State } = useStakeTokens(tokenAddress)
    console.log("here is rewards to assign:", rewardsToAssign)
    const handleQuestionnaireCreation = () => {
        console.log("here is rewards to assign:")
        const rewardsToAssignAsWei = utils.parseEther(rewardsToAssign.toString())
        console.log("here is rewards to assign:", rewardsToAssignAsWei)
        return approveAndStake(rewardsToAssignAsWei.toString())
    }

    const rewards = useRewardsCheck(tokenAddress)
    const formattedRewards: number = rewards ? parseFloat(formatUnits(rewards, 18)) : 0

    const { answerQuestions, state: answerState } = useAnswer("0")
    const handleAnswer = () => {
        console.log("answerd")
        answerQuestions()
    }

    const { claimRewards, state: claimRewardsState } = useClaimRewards("0")
    const handleClaimRewards = () => {
        console.log("rewards claimed")
        claimRewards()
    }
    const isMining = approveAndStakeErc20State.status === "Mining" ||
        answerState.status === "Mining" ||
        claimRewardsState.status === "Mining"

    console.log(isMining)
    let temp
    return (
        <>
            <div>
                <WalletBalance token={token}></WalletBalance>
                <form onSubmit={handleQuestionnaireCreation}>
                    <label>
                        rewards to assign:
                        <input type="number" value={temp} onChange={handleInputChangeRewardsToAssign}>
                        </input>
                    </label>
                    <label>
                        Maximun answers to accept:
                        <input type="number" value={temp} onChange={handleInputChangeMaxAnswers}>
                        </input>
                    </label>
                    <label>
                        Deadline:
                        <input type="number" value={temp} onChange={handleInputChangeDeadline}>
                        </input>
                    </label>
                    <input type="submit" value="Creat Questionnaire!!!"></input>
                </form>
            </div>
            <div>
                <p>Your rewards:{formattedRewards}</p>
            </div>
            <div>
                <Button
                    onClick={handleAnswer}
                    color="primary"
                    size="large"
                    disabled={isMining}>
                    {isMining ? <CircularProgress size={26} /> : "Answer"}
                </Button>
            </div>
            {formattedRewards === 0 ?
                <p>No Rewards to claim!</p> :
                <div>
                    <Button
                        onClick={handleClaimRewards}
                        color="primary"
                        size="large"
                        disabled={isMining}>
                        {isMining ? <CircularProgress size={26} /> : "Claim Rewards"}
                    </Button>
                </div>
            }
        </>
    )
}
