import { useEffect, useState } from "react"
import { useEthers, useContractFunction } from "@usedapp/core"
import { constants, utils } from "ethers"
import networkMapping from "../chain-info/deployments/map.json"
import MainRouter from "../chain-info/contracts/MainRouter.json"
import { Contract } from "@ethersproject/contracts"

export const useRewardsCheck = (tokenAddress: string) => {
    // address
    // abi
    // chainId
    const { abi } = MainRouter
    const { chainId } = useEthers()
    const tokenFarmAddress = chainId ? networkMapping["4"]["MainRouter"][0] : constants.AddressZero
    const tokenFarmInterface = new utils.Interface(abi)
    const tokenFarmContract = new Contract(tokenFarmAddress, tokenFarmInterface)

    const rewardsCheck = () => {
        console.log(tokenFarmAddress)
        const rewards = checkRewards()
        console.log("Your rewards:")
        console.log(rewards)
        return rewards
    }

    // Check Rewards
    const { send: checkRewards, state: rewardsCheckState } =
        useContractFunction(tokenFarmContract, "myRewards", {
            transactionName: "Check Rewards",
        })

    const [rewards, setRewards] = useState("20")
    return { rewardsCheck, rewards }

}
