import React, { useState, useEffect } from "react"
import { Token } from "../Main"
import { useEthers, useTokenBalance } from "@usedapp/core"
import { formatUnits } from "@ethersproject/units"
import { Button, Input, CircularProgress } from "@material-ui/core"
import { useStakeTokens, useRewardsCheck, useAnswer } from "../../hooks"
import { utils } from "ethers"

export interface StakeFormProps {
    token: Token
}

export const StakeForm = ({ token }: StakeFormProps) => {
    const { address: tokenAddress, name } = token
    const { account } = useEthers()
    const tokenBalance = useTokenBalance(tokenAddress, account)
    const formattedTokenBalance: number = tokenBalance ? parseFloat(formatUnits(tokenBalance, 18)) : 0

    const [amount, setAmount] = useState<number | string | Array<number | string>>(0)
    const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const newAmount = event.target.value === "" ? "" : Number(event.target.value)
        setAmount(newAmount)
        console.log(newAmount)
    }

    const { approveAndStake, state: approveAndStakeErc20State } = useStakeTokens("0")
    const handleStakeSubmit = () => {
        const amountAsWei = utils.parseEther(amount.toString())
        return approveAndStake(amountAsWei.toString())
    }

    const { rewardsCheck, rewards } = useRewardsCheck("0")
    const handleRewardsCheck = () => {
        console.log(rewards)
        return rewardsCheck()
    }

    const { answerQuestions, state: answerState } = useAnswer("0")
    const handleAnswer = () => {
        console.log("answerd")
        answerQuestions()
    }

    const handleClaimRewards = () => {
        console.log("rewards claimed")
    }
    const isMining = approveAndStakeErc20State.status === "Mining"


    return (
        <>
            <div>
                <Input
                    onChange={handleInputChange} />
                <Button
                    onClick={handleStakeSubmit}
                    color="primary"
                    size="large"
                    disabled={isMining}>
                    {isMining ? <CircularProgress size={26} /> : "Stake!!!"}
                </Button>
            </div>
            <div>
                <Button
                    onClick={handleRewardsCheck}
                    color="primary"
                    size="large"
                    disabled={isMining}>
                    {isMining ? <CircularProgress size={26} /> : "Check rewards"}
                </Button>
            </div>
            <div>
                <Button
                    onClick={handleAnswer}
                    color="primary"
                    size="large"
                    disabled={isMining}>
                    {isMining ? <CircularProgress size={26} /> : "Answer"}
                </Button>
            </div>
            <div>
                <Button
                    onClick={handleClaimRewards}
                    color="primary"
                    size="large"
                    disabled={isMining}>
                    {isMining ? <CircularProgress size={26} /> : "Claim Rewards"}
                </Button>
            </div>
        </>
    )
}
