import { useEffect, useState } from "react"
import { useEthers, useContractFunction } from "@usedapp/core"
import { constants, utils } from "ethers"
import MainRouter from "../chain-info/contracts/MainRouter.json"
import { Contract } from "@ethersproject/contracts"

export const useStakeTokens = (tokenAddress: string) => {
    // address
    // abi
    // chainId
    const { chainId } = useEthers()
    const { abi } = MainRouter
    const tokenFarmAddress = "0xfdbdcdaa565ac2fd517318e31f90dbfde9c76611"
    const tokenFarmInterface = new utils.Interface(abi)
    const tokenFarmContract = new Contract(tokenFarmAddress, tokenFarmInterface)

    const approveAndStake = (amount: string) => {
        console.log("amout to stake")
        console.log(amount)
        setAmountToStake(amount)
        return stakeSend(amount)
    }

    // stake
    const { send: stakeSend, state: stakeState } =
        useContractFunction(tokenFarmContract, "stake", {
            transactionName: "Stake Tokens",
        })

    const [amountToStake, setAmountToStake] = useState("0")

    //useEffect
    const [state, setState] = useState(stakeState)
    useEffect(() => {
        stakeSend(amountToStake)
    }, [amountToStake])


    return { approveAndStake, state }
}
