import { useEthers, useCall } from "@usedapp/core"
import { constants, utils } from "ethers"
import networkMapping from "../chain-info/deployments/map.json"
import MainRouter from "../chain-info/contracts/MainRouter.json"
import { Contract } from "@ethersproject/contracts"
import { formatUnits } from "@ethersproject/units"

export const useQuestionsList = (tokenAddress: string) => {
    // address
    // abi
    // chainId
    const { abi } = MainRouter
    const { account, chainId } = useEthers()
    const tokenFarmAddress = chainId ? networkMapping["4"]["MainRouter"][0] : constants.AddressZero
    const tokenFarmInterface = new utils.Interface(abi)
    const tokenFarmContract = new Contract(tokenFarmAddress, tokenFarmInterface)

    // Check Rewards
    //    console.log("in", tokenAddress, tokenFarmAddress)
    const { value: results } =
        useCall({
            contract: tokenFarmContract,
            method: 'getQuestionsStatus',
            args: []
        }) ?? {}
    const formattedRewards: number = results[2][1] ? parseFloat(formatUnits(results[2][1], 0)) : 0
    const questionsList = results[0]
    const toShow = []
    for (var i = 0; i < questionsList.length; i++) {
        const formattedAnswered: number = results[1][i] ? parseFloat(formatUnits(results[1][i], 0)) : 0
        const formattedMaxAnswers: number = results[2][i] ? parseFloat(formatUnits(results[2][i], 0)) : 0
        toShow.push({ "id": i, "status": results[0][i], "spots": formattedAnswered, "maxAnswers": formattedMaxAnswers })
    }
    return toShow
    //   return [{ 'id': 0, 'status': 0, 'spots': 11, 'maxAnswers': 20 }, { 'id': 1, 'status': 0, 'spots': 11, 'maxAnswers': 20 }, { 'id': 2, 'status': 1, 'spots': 11, 'maxAnswers': 20 }]
}
