// SPDX-License-Identifier: MIT

pragma solidity >=0.6.0 <0.9.0;

contract MainRouter {
    //    using SafeMathChainlink for uint256;

    mapping(address => uint256) public stakersToAmout;
    address[] public stakers;
    mapping(address => uint256) public answersToAmout;
    address[] public answers;

    address public owner;

    constructor() public {
        owner = msg.sender;
    }

    function stake() public payable {
        uint256 minStake = 10000000000000000;
        require(msg.value >= minStake, "You need to spend more token!");
        stakersToAmout[msg.sender] += msg.value;
        stakers.push(msg.sender);
    }

    function answer() public {
        uint256 reward = 10000000000000000;
        answersToAmout[msg.sender] += reward;
        answers.push(msg.sender);
    }

    function myRewards() public view returns (uint256) {
        require(answersToAmout[msg.sender] > 0, "No rewards found!");
        return answersToAmout[msg.sender];
    }

    function claimRewards() public payable {
        uint256 amount = answersToAmout[msg.sender];
        require(amount > 0, "No rewards found!");
        payable(msg.sender).transfer(amount);
        answersToAmout[msg.sender] = 0;
    }
    //answers arrary needs to be modified after claim
}
